import Grid from '@material-ui/core/Grid';
import { CssBaseline, Paper, Avatar, Typography, Button, InputLabel, FormControl, Input, Link } from '@material-ui/core';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import DialogContent from "@material-ui/core/DialogContent";
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';

const Profile='/Profile';
const useStyles = makeStyles(theme => ({
  button: {
    display: 'block',
    marginTop: theme.spacing(2),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

export default function TaskFilters() {
  const classes = useStyles();
  const [age, setAge] = React.useState('');
  const [open, setOpen] = React.useState(false);

  const handleChange = event => {
    setAge(event.target.value);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  return (
    <React.Fragment>
        <Grid container className="background">
            <Grid item xs={12} sm={12} md={6} elevation={6}>
                <CssBaseline />
                <main className="layout">
                    <Paper className="paper">
                        <Typography variant="headline">NEW TASK</Typography>
                        <div>
                          <Button className={classes.button} onClick={handleOpen}>
                            Open the select
                          </Button>
                          <FormControl className={classes.formControl}>
                            <InputLabel id="demo-controlled-open-select-label">Age</InputLabel>
                            <Select
                              labelId="demo-controlled-open-select-label"
                              id="demo-controlled-open-select"
                              open={open}
                              onClose={handleClose}
                              onOpen={handleOpen}
                              value={age}
                              onChange={handleChange}
                            >
                              <MenuItem value="">
                                <em>None</em>
                              </MenuItem>
                              <MenuItem value={10}>Ten</MenuItem>
                              <MenuItem value={20}>Twenty</MenuItem>
                              <MenuItem value={30}>Thirty</MenuItem>
                            </Select>
                          </FormControl>
                        </div>
                       <br/>
                       <Button type="submit" variant="contained" color="black" className="submit" component={Link} to={Profile}>
                           volver
                       </Button>
                   </Paper>
               </main>
           </Grid>
       </Grid>
   </React.Fragment>
  );
}
